function submitForm() {
  var button = document.querySelector('#submitButton');
  var spinner = '<span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>' +
               '<span class="sr-only">Loading...</span>';
  var originalContent = button.childNodes[1].cloneNode(true);

  // Disable the button and replace its content with the spinner
  button.disabled = true;
  button.innerHTML = spinner;

  var e = document.getElementById("myForm"),
      t = new FormData(e),
      s = new XMLHttpRequest();

  s.open(
    "POST",
    "https://script.google.com/macros/s/AKfycbyRiGTZCjojfjZJS-Oi1pbxYmRBdCZVa-m_qhHpqMvqWHG9HpDmT5tfVRG86f0aGY7e/exec"
  );

  s.onload = function () {
    if (s.status === 200) {
      document.getElementById("successMessage").className = "alert alert-success";
      document.getElementById("successMessage").innerText = "Form submitted successfully.";
      resetFormInputs();
    } else {
      document.getElementById("successMessage").className = "alert alert-danger";
      document.getElementById("successMessage").innerText = "Error submitting form.";
    }

    // Enable the button and revert its content back to original
    button.disabled = false;
    button.innerHTML = '';
    button.appendChild(originalContent);
  };

  s.send(t);
  return false;
}

function resetFormInputs() {
  document.getElementById("myForm").reset();
}
